// Bootstrap cliui with CommonJS dependencies:
import { cliui } from 'cliui/build/lib/index.js'
import { wrap, stripAnsi } from 'cliui/build/lib/string-utils.js'

export default function ui (opts) {
  return cliui(opts, {
    stringWidth: (str) => {
      return [...str].length
    },
    stripAnsi,
    wrap
  })
}
