'use strict';


var loader = require('js-yaml/lib/loader');
var dumper = require('js-yaml/lib/dumper');


function renamed(from, to) {
  return function () {
    throw new Error('Function yaml.' + from + ' is removed in js-yaml 4. ' +
      'Use yaml.' + to + ' instead, which is now safe by default.');
  };
}


module.exports.Type                = require('js-yaml/lib/type');
module.exports.Schema              = require('js-yaml/lib/schema');
module.exports.FAILSAFE_SCHEMA     = require('js-yaml/lib/schema/failsafe');
module.exports.JSON_SCHEMA         = require('js-yaml/lib/schema/json');
module.exports.CORE_SCHEMA         = require('js-yaml/lib/schema/core');
module.exports.DEFAULT_SCHEMA      = require('js-yaml/lib/schema/default');
module.exports.load                = loader.load;
module.exports.loadAll             = loader.loadAll;
module.exports.dump                = dumper.dump;
module.exports.YAMLException       = require('js-yaml/lib/exception');

// Removed functions from JS-YAML 3.0.x
module.exports.safeLoad            = renamed('safeLoad', 'load');
module.exports.safeLoadAll         = renamed('safeLoadAll', 'loadAll');
module.exports.safeDump            = renamed('safeDump', 'dump');
